package types_test

import (
	"blockexchange/types"
	"encoding/json"
	"testing"

	"github.com/stretchr/testify/assert"
)

func TestUnmarshalMediafile(t *testing.T) {
	str := `{"data":"","mod_name":"default","name":"torch_ceiling.obj"}`

	mf := &types.Mediafile{}
	err := json.Unmarshal([]byte(str), mf)
	assert.NoError(t, err)
	assert.NotNil(t, mf.Data)
}
